const converter = require('number-to-words');
const chalk = require('chalk');

const separator = chalk.grey('====================================================================================================\n')
const whitespace = "  "
let tabNum = 2

const logMessages = {
    startingPlugin: (xrayMode) => {
        console.log(separator);
        console.log(chalk.white('  Cypress Xray Junit Reporter | Creating XML report'));
        console.log(chalk.white('  -------------------------------------------------\n'));
        if (xrayMode) { console.log(chalk.white('    â³ XrayMode Enabled -> Retrieving suites information from Root Suite... ')); } else {
            console.log(chalk.white('    â³ XrayMode Disabled -> Retrieving suites information from Root Suite without searching for JiraKeys... '));
        }
    },
    endPlugin: () => {
        console.log(`\n${whitespace.repeat(tabNum)}------------------------------------`);
        console.log(`${whitespace.repeat(tabNum)}${chalk.green('All suites have been parsed correctly!\n')}`);
        console.log(`${separator}`);
    },
    warning: (shortenLogMode, suitesNum, message) => {
        if (!shortenLogMode) {
            console.log(`${whitespace.repeat(suitesNum + tabNum)}${chalk.black('âš ï¸  ' + message)}`);
        } else {
            console.log(`${whitespace.repeat(tabNum)}${chalk.black('âš ï¸  ' + message)}`);
        }
    },
    missingError: (shortenLogMode, suitesNum,) => {
        const message = 'â€¼ Missing jira key in at least one testcase'
        if (!shortenLogMode) {
            console.log(`${whitespace.repeat(suitesNum + tabNum)}${chalk.red(message)}`);
        } else {
            console.log(`${whitespace.repeat(tabNum)}${chalk.red(message)}`);
        }
    },
    skippedError: (shortenLogMode, testArray) => {
        let wsNum
        if (shortenLogMode) {
            wsNum = tabNum
            const message = `â€¼ Skipping testcases:\n${whitespace.repeat(wsNum)}` + chalk.cyan('- ' + testArray.join(`,\n${whitespace.repeat(wsNum)}- `))
            console.log(`${whitespace.repeat(wsNum)}${chalk.red(message)}`);
        }
    },
    skippedTestcase: (shortenLogMode, suitesNum, title) => {
        if (!shortenLogMode) {
            console.log(`${whitespace.repeat(suitesNum + tabNum)}${chalk.black('ã€° Skipping testcase: ')} ${chalk.white(title)}`);
        }
    },
    analyzedTestcase: (shortenLogMode, suitesNum, title) => {
        if (!shortenLogMode) {
            console.log(`${whitespace.repeat(suitesNum + tabNum)}${chalk.black('ã€° Properly analyzed testcase:')} ${chalk.white(title)}`);
        }
    },
    foundingSuite: (shortenLogMode, suitesNum, suitesQuantity) => {
        if (!shortenLogMode) {
            console.log(`\n${whitespace.repeat(suitesNum + tabNum)}${chalk.black('ã€° Founded ' + chalk.yellow(converter.toWords(suitesQuantity)) + ' testsuite(s), keep scraping..')}`);
        }
    },
    analyzingSuite: (shortenLogMode, suitesNum, title) => {
        if (!shortenLogMode) {
            console.log(`${whitespace.repeat(suitesNum + tabNum)}${chalk.cyanBright('ã€° Analyzing testsuite: ') + chalk.white(title)}`);
            console.log(`${whitespace.repeat(suitesNum + tabNum)}${chalk.white('ðŸ” Looking for testsuite or testcase...')}`);
        }
    },
    endSuite: (shortenLogMode, suitesNum, title) => {
        if (!shortenLogMode) {
            console.log(`${whitespace.repeat(suitesNum + tabNum)}${chalk.green('âœ” Successfully analyzed suite:')} ${chalk.white(title)}\n`)
        }
    },
};

module.exports = logMessages;
